namespace SmartBotBlazorApp.Components.RobotMovementInput
{
    public class debugFunctions
    {
        public static string GetExampleImage()
        {
            var base64PngImg = @"";
            var image = $"data: image / png; base64,{base64PngImg}";

            return image;
        }
        public static double[] GenerateTestMeasurements(int precision)
        {
            var random = new Random();
            var data = new double[7];

            for (int i = 0; i < data.Length; i++)
            {
                data[i] = Math.Round(random.NextDouble(), precision, MidpointRounding.ToEven); ;
            }

            return data;
        }
        public static ushort[] GenerateTestData(int resolution)
        {
            var random = new Random();
            ushort[] data = new ushort[resolution * resolution];

            for (int i = 0; i < data.Length; i++)
            {
                data[i] = (ushort)random.Next(1, 4000);
            }

            return data;
        }

        public static ushort[] GenerateTestDataAscend(int resolution)
        {
            ushort[] data = new ushort[resolution * resolution];

            int index = 0;

            for (int i = 0; i < resolution; i++)
            {
                for (int j = 0; j < resolution; j++)
                {
                    data[index] = (ushort)(i * 125 + j);
                    index++;
                }
            }

            return data;
        }


        static public double[] GenerateTestDataAscendDouble(int resolution)
        {
            double[] data = new double[resolution * resolution];

            int index = 0;

            for (int i = 0; i < resolution; i++)
            {
                for (int j = 0; j < resolution; j++)
                {
                    data[index] = (ushort)(i * 125 + j);
                    index++;
                }
            }

            return data;
        }
    }
}
